import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles.css';

const Header = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token')); // Initial check for token

    useEffect(() => {
        const token = localStorage.getItem('token');
        setIsAuthenticated(!!token); // Update authentication status whenever component mounts or token changes
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token'); // Remove token
        setIsAuthenticated(false); // Update authentication status
    };

    return (
        <header className="header">
            <h1>Personal Finance Management Tracking</h1>
            <nav className="nav-links">
                {isAuthenticated ? (
                    <Link to="/logout" className="nav-link" onClick={handleLogout}>
                        Logout
                    </Link>
                ) : (
                    <>
                        <Link to="/login" className="nav-link">
                            Login
                        </Link>
                        <Link to="/register" className="nav-link">
                            Register
                        </Link>
                    </>
                )}
            </nav>
        </header>
    );
};

export default Header;
