import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles.css';
import '../pfm.css';

const Goals = () => {
  const [goals, setGoals] = useState([]);
  const [form, setForm] = useState({
    name: '',
    targetAmount: '',
    savedAmount: '',
    description: '',
    deadline: '',
  });

  useEffect(() => {
    const fetchGoals = async () => {
      try {
        const response = await axios.get('/api/goals');
        setGoals(response.data);
      } catch (error) {
        console.error('Error fetching goals:', error);
      }
    };

    fetchGoals();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prevForm) => ({ ...prevForm, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/goals', form);
      setGoals((prevGoals) => [...prevGoals, response.data]);
      setForm({
        name: '',
        targetAmount: '',
        savedAmount: '',
        description: '',
        deadline: '',
      });
    } catch (error) {
      console.error('Error creating goal:', error);
    }
  };

  return (
    <div className="app">
      <header className="header">
        <h1>Goals</h1>
      </header>
      <div className="main-content">
        <div className="content">
          <ul>
            {goals.map((goal) => (
              <li key={goal._id} className="register-container">
                <h2>{goal.name}</h2>
                <p><strong>Target Amount:</strong> ${goal.targetAmount}</p>
                <p><strong>Saved Amount:</strong> ${goal.savedAmount}</p>
                <p><strong>Description:</strong> {goal.description || 'No description'}</p>
                <p><strong>Deadline:</strong> {new Date(goal.deadline).toLocaleDateString()}</p>
              </li>
            ))}
          </ul>
          <form onSubmit={handleSubmit} className="register-form">
            <h2>Create New Goal</h2>
            <div className="form-group">
              <label>Name:</label>
              <input
                type="text"
                name="name"
                value={form.name}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Target Amount:</label>
              <input
                type="number"
                name="targetAmount"
                value={form.targetAmount}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Saved Amount:</label>
              <input
                type="number"
                name="savedAmount"
                value={form.savedAmount}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label>Description:</label>
              <textarea
                name="description"
                value={form.description}
                onChange={handleChange}
              ></textarea>
            </div>
            <div className="form-group">
              <label>Deadline:</label>
              <input
                type="date"
                name="deadline"
                value={form.deadline}
                onChange={handleChange}
                required
              />
            </div>
            <button type="submit" className="submit-btn">Create Goal</button>
          </form>
        </div>
      </div>
      <footer className="footer">
        <p>&copy; 2024 Your Company</p>
      </footer>
    </div>
  );
};

export default Goals;
