import React, { useState } from 'react';
import axios from 'axios';

const Report = () => {
  const [monthlyReport, setMonthlyReport] = useState(null);
  const [yearlyReport, setYearlyReport] = useState(null);
  const [error, setError] = useState(null);

  const [month, setMonth] = useState('');
  const [year, setYear] = useState('');

  // Fetch Monthly Report
  const fetchMonthlyReport = async () => {
    try {
      setError(null);
      const response = await axios.get(`/api/reports/monthly/${year}/${month}`);
      setMonthlyReport(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Error fetching monthly report');
    }
  };

  // Fetch Yearly Report
  const fetchYearlyReport = async () => {
    try {
      setError(null);
      const response = await axios.get(`/api/reports/yearly/${year}`);
      setYearlyReport(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Error fetching yearly report');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Reports</h2>

      {/* Monthly Report */}
      <div>
        <h3>Monthly Report</h3>
        <label>
          Month (MM):
          <input
            type="text"
            value={month}
            onChange={(e) => setMonth(e.target.value)}
            placeholder="e.g., 01 for January"
          />
        </label>
        <label>
          Year (YYYY):
          <input
            type="text"
            value={year}
            onChange={(e) => setYear(e.target.value)}
            placeholder="e.g., 2024"
          />
        </label>
        <button onClick={fetchMonthlyReport}>Get Monthly Report</button>
        {monthlyReport && (
          <div>
            <h4>Results for {monthlyReport.month}/{monthlyReport.year}</h4>
            <p>Income: ${monthlyReport.income}</p>
            <p>Expense: ${monthlyReport.expense}</p>
          </div>
        )}
      </div>

      {/* Yearly Report */}
      <div style={{ marginTop: '20px' }}>
        <h3>Yearly Report</h3>
        <label>
          Year (YYYY):
          <input
            type="text"
            value={year}
            onChange={(e) => setYear(e.target.value)}
            placeholder="e.g., 2024"
          />
        </label>
        <button onClick={fetchYearlyReport}>Get Yearly Report</button>
        {yearlyReport && (
          <div>
            <h4>Results for {yearlyReport.year}</h4>
            <p>Income: ${yearlyReport.income}</p>
            <p>Expense: ${yearlyReport.expense}</p>
          </div>
        )}
      </div>

      {/* Error Handling */}
      {error && (
        <div style={{ color: 'red', marginTop: '20px' }}>
          <p>{error}</p>
        </div>
      )}
    </div>
  );
};

export default Report;
