import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles.css';
import '../pfm.css';

const Budgets = () => {
  const [budgets, setBudgets] = useState([]);
  const [form, setForm] = useState({
    name: '',
    amount: '',
    description: '',
    startDate: '',
    endDate: '',
  });

  const [editingBudget, setEditingBudget] = useState(null);

  useEffect(() => {
    const fetchBudgets = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/budgets');
        setBudgets(response.data);
      } catch (error) {
        console.error('Error fetching budgets:', error);
      }
    };

    fetchBudgets();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prevForm) => ({ ...prevForm, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingBudget) {
        const response = await axios.put(`http://localhost:5000/api/budgets/${editingBudget._id}`, form);
        setBudgets((prevBudgets) =>
          prevBudgets.map((budget) =>
            budget._id === editingBudget._id ? response.data : budget
          )
        );
        setEditingBudget(null);
      } else {
        const response = await axios.post('/api/budgets', form);
        setBudgets((prevBudgets) => [...prevBudgets, response.data]);
      }
      setForm({
        name: '',
        amount: '',
        description: '',
        startDate: '',
        endDate: '',
      });
    } catch (error) {
      console.error('Error saving budget:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/budgets/${id}`);
      setBudgets((prevBudgets) => prevBudgets.filter((budget) => budget._id !== id));
    } catch (error) {
      console.error('Error deleting budget:', error);
    }
  };

  const handleEdit = (budget) => {
    setEditingBudget(budget);
    setForm({
      name: budget.name,
      amount: budget.amount,
      description: budget.description || '',
      startDate: budget.startDate.split('T')[0],
      endDate: budget.endDate.split('T')[0],
    });
  };

  return (
    <div className="app">
      <header className="header">
        <h1>Budgets</h1>
      </header>
      <div className="main-content">
        <div className="content">
          <ul>
            {budgets.map((budget) => (
              <li key={budget._id} className="register-container">
                <h2>{budget.name}</h2>
                <p><strong>Amount:</strong> ${budget.amount}</p>
                <p><strong>Description:</strong> {budget.description || 'No description'}</p>
                <p><strong>Start Date:</strong> {new Date(budget.startDate).toLocaleDateString()}</p>
                <p><strong>End Date:</strong> {new Date(budget.endDate).toLocaleDateString()}</p>
                <button onClick={() => handleEdit(budget)} className="submit-btn">Edit</button>
                <button onClick={() => handleDelete(budget._id)} className="submit-btn">Delete</button>
              </li>
            ))}
          </ul>
          <form onSubmit={handleSubmit} className="register-form">
            <h2>{editingBudget ? 'Edit Budget' : 'Create New Budget'}</h2>
            <div className="form-group">
              <label>Name:</label>
              <input
                type="text"
                name="name"
                value={form.name}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Amount:</label>
              <input
                type="number"
                name="amount"
                value={form.amount}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Description:</label>
              <textarea
                name="description"
                value={form.description}
                onChange={handleChange}
              ></textarea>
            </div>
            <div className="form-group">
              <label>Start Date:</label>
              <input
                type="date"
                name="startDate"
                value={form.startDate}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>End Date:</label>
              <input
                type="date"
                name="endDate"
                value={form.endDate}
                onChange={handleChange}
                required
              />
            </div>
            <button type="submit" className="submit-btn">{editingBudget ? 'Update Budget' : 'Create Budget'}</button>
          </form>
        </div>
      </div>
      <footer className="footer">
        <p>&copy; 2024 Your Company</p>
      </footer>
    </div>
  );
};

export default Budgets;
