import React, { useState } from 'react';
import api from '../utils/apiService'; // Import the helper
import '../styles.css';
import '../pfm.css';

const AddTransaction = ({ onAdd }) => {
    const [description, setDescription] = useState('');
    const [amount, setAmount] = useState('');
    const [type, setType] = useState('income');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newTransaction = { description, amount: parseFloat(amount), type };
        try {
            const response = await api.post('/transactions', newTransaction); // Use authenticated request
            await onAdd(response.data);
            setDescription('');
            setAmount('');
        } catch (err) {
            console.error('Error adding transaction:', err);
        }
    };

    return (
        <div className="register-container">
            {/* Rest of the form remains unchanged */}
        </div>
    );
};

export default AddTransaction;
